mapper.update:
update admin_account
set
  is_delete = 0

  ,account = #{account,jdbcType=VARCHAR}


  , name = #{name}


  , password = #{password,jdbcType=VARCHAR}

where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, account, is_delete
from admin_account
where id = #{id,jdbcType=VARCHAR}
