mapper.update:
update bdt_system
set ps = #{ps,jdbcType=INTEGER},
  phxs = #{phxs,jdbcType=DECIMAL},
  started = #{started,jdbcType=BIT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, ps, phxs, started, admin_id
from bdt_system
where id = #{id,jdbcType=INTEGER}

mapper.update:
