mapper.insert:
insert into result_data (id, create_time, table_no, 
  battle_no, fit_no, tzfx, 
  tzxt, tzzh, tzje, tzzt, 
  tzjg, yxje, yssy, sjsy
  )
values (#{id,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{tableNo,jdbcType=INTEGER}, 
  #{battleNo,jdbcType=INTEGER}, #{fitNo,jdbcType=INTEGER}, #{tzfx,jdbcType=INTEGER}, 
  #{tzxt,jdbcType=INTEGER}, #{tzzh,jdbcType=VARCHAR}, #{tzje,jdbcType=VARCHAR}, #{tzzt,jdbcType=BIT}, 
  #{tzjg,jdbcType=INTEGER}, #{yxje,jdbcType=DECIMAL}, #{yssy,jdbcType=DECIMAL}, #{sjsy,jdbcType=DECIMAL}
  )

mapper.select:
select id, create_time, table_no, battle_no, fit_no, tzfx, tzxt, tzzh, tzje, tzzt,
tzjg, yxje, yssy, sjsy
from result_data
WHERE 1 = 1

  AND create_time >= #{createTime}
  AND create_time  <  date_sub(#{createTime},interval -1 day)


  AND tzxt = #{tzxt}


  AND tzzh = #{tzzh}

ORDER BY create_time DESC

select id, create_time, table_no, battle_no, fit_no, tzfx, tzxt, tzzh, tzje, tzzt, tzjg, yxje, yssy, sjsy from result_data WHERE tzzt IS NULL
AND table_no = #{tableNo}
ORDER BY create_time DESC

mapper.update:
update result_data
set
  tzzt = #{tzzt}
where id = #{id}
