mapper.insert:
insert into status_request (table_no, battle_no,
  fit_no, status, update_time
  )
values (#{tableNo,jdbcType=INTEGER}, #{battleNo,jdbcType=INTEGER},
  #{fitNo,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, now()
  )

mapper.select:
select id, table_no, battle_no, fit_no, status, update_time
from status_request
where id = #{id,jdbcType=INTEGER}

SELECT
    count( 1 )
FROM
    status_request
WHERE
    table_no = #{tableNo}
    AND battle_no = #{battleNo}
    AND fit_no = #{fitNo}
    AND `status` = #{status}
    AND update_time >= DATE_ADD( NOW( ), INTERVAL - 1 HOUR )
