mapper.insert:
insert into tz_system (id, tzxt, started, fha, fhb, fhc, fhd, xh, admin_id
  )
values (#{id,jdbcType=INTEGER}, #{tzxt,jdbcType=INTEGER}, #{started,jdbcType=BIT},
#{fha}, #{fhb}, #{fhc}, #{fhd}, #{xh}, #{adminId}
  )

mapper.select:
select id, tzxt, started, fha, fhb, fhc, fhd, xh, admin_id
from tz_system
where id = #{id,jdbcType=INTEGER}

mapper.update:
update tz_system
set started = #{started}

  ,fha = #{fha}


  ,fhb = #{fhb}


  ,fhc = #{fhc}


  ,fhd = #{fhd}


  ,xh = #{xh}

where tzxt = #{tzxt} AND admin_id = #{adminId}

