mapper.update:
update wx_send_record
set type = #{type,jdbcType=INTEGER},
  content = #{content,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, type, content, create_time
from wx_send_record
where id = #{id,jdbcType=INTEGER}
